using System;using System.Collections.Generic;using System.Linq;using System.Security.Claims;using System.Threading.Tasks;using MediatR;using Microsoft.AspNetCore.Authorization;using Microsoft.AspNetCore.Http;using Microsoft.AspNetCore.Mvc;using Monotone.Services.Application.Queries;namespace Monotone.Controllers.Notification{    [Authorize]    [Route("api/v1/[controller]/[action]")]    [ApiController]    public class UserController : ControllerBase    {        private readonly IMediator _mediator;        public UserController(IMediator mediator)        {            _mediator = mediator;        }                [HttpGet]        public async Task<IActionResult> GetOnlineStatusForUser([FromQuery] string userId)        {            try            {            var result = await _mediator.Send(new GetOnlineStatusForUserQuery(userId));            if (result.IsT0)            {                return Ok(result.AsT0);            }            return BadRequest(result.AsT1.Message);            }            catch (Exception e)            {                return StatusCode(StatusCodes.Status500InternalServerError, e.Message);            }                    }                [HttpGet]        public async Task<IActionResult> SearchUserByUsername([FromQuery] string searchKey)        {            try            {                var result = await _mediator.Send(new SearchUserByUsernameQuery(searchKey));                if (result.IsT0)                {                    return Ok(result.AsT0);                }                return BadRequest(result.AsT1.Message);            }            catch (Exception e)            {                return StatusCode(StatusCodes.Status500InternalServerError, e.Message);            }                    }            }}